mapper.sql:
SELECT T.order_id, T.order_status, T.total_pay_amount, T.pay_time, T.create_time, T.goods_name, T.nick_name, T.open_id, T.receiver, T.contact_phone, T.receive_addr, T.spec_param, T.total_count, T.express_id FROM ring_order_info T WHERE 1=1
AND T.create_time >= ${params.createTimeStar}
AND T.create_time <= ${params.createTimeEnd}
AND T.ORDER_ID = '${params.orderId}'
AND T.order_status = '${params.orderStatus}'
AND T.contact_phone = '${params.contactPhone}'
AND T.express_id LIKE '%${params.expressId}%'
AND T.order_id = ${params.orderId}
